mapper.insert:

       	    select ifnull(max(CAST(SERVICE_ID AS DECIMAL)), 0) + 1
			from tb_service_site

        INSERT INTO tb_service_site 
        (`SERVICE_ID`, `SERVICE_NAME`, `MANAGER_NAME`, `MANAGER_TEL`, `MANAGER_EMAIL`, `SERVICE_IP`, `PERMISSION_KEY`, `CREATE_DATETIME`) 
        VALUES 
        (
			  LPAD(#{nextval}, 6, 0)
			, #{serviceName}
			, #{managerName}
			, #{managerTel}
			, #{managerEmail}
			, #{serviceIp}
			, #{permissionKey}        
			, NOW()        
        )

mapper.update:
UPDATE tb_service_site
   SET   SERVICE_NAME = #{serviceName}
       , MANAGER_NAME = #{managerName}
       , MANAGER_TEL = #{managerTel}
       , MANAGER_EMAIL = #{managerEmail}
       , SERVICE_IP = #{serviceIp}
       , PERMISSION_KEY = #{permissionKey}
WHERE  SERVICE_ID = #{serviceId}
