			, #{serviceName}
			, #{managerName}
			, #{managerTel}
			, #{managerEmail}
			, #{serviceIp}
			, #{permissionKey}        
        )

mapper.update:
UPDATE tb_search_log
   SET   SERVICE_NAME = #{serviceName}
       , MANAGER_NAME = #{managerName}
       , MANAGER_TEL = #{managerTel}
       , MANAGER_EMAIL = #{managerEmail}
       , SERVICE_IP = #{serviceIp}
       , PERMISSION_KEY = #{permissionKey}
WHERE  SERVICE_ID = #{serviceId}
